import React, { useState, useEffect } from "react";
import "../styles/Contact.css";

const About = () => {
    const [mousePos, setMousePos] = useState(0);
    useEffect(() => {
        const handleMouseMove = (e) => {
            console.log(e);
            if (
                e.clientX < document.getElementById("card").style.left &&
                e.clientX > 300 &&
                e.clientY > 140 &&
                e.clientY < 490
            ) {
                setMousePos({ x: e.clientX, y: e.clientY });
            }
        };
        document
            .getElementById("card")
            .addEventListener("mousemove", handleMouseMove);
        return () => {
            document
                .getElementById("card")
                .removeEventListener("mousemove", handleMouseMove);
        };
    }, []);
    return (
        <div className="Contact modules">
            <div id="card" className="card">
                <div
                    id="circle"
                    style={{
                        left: (mousePos.x / window.innerWidth) * 100 + "%",
                        top: (mousePos.y / window.innerHeight) * 100 + "%",
                    }}
                ></div>
                <div className="block">
                    <h3>
                        ({mousePos.x}, {mousePos.y})
                    </h3>
                    <h3>Contact</h3>
                    <a href="mailto:m.claretdamien@gmail">
                        claretdamien@gmail.com
                    </a>
                    <p>0633191067</p>
                    <h4>Bretagne, Morbihan</h4>
                </div>
            </div>
            <div className="contactInputs">
                <input type="email"></input>
            </div>
        </div>
    );
};

export default About;
