import React from "react";
import { Link } from "react-router-dom";
import "./styles/NavBar.css";

const NavBar = () => {
    let w = window.innerWidth;
    let h = window.innerWidth;
    let nbrClick;
    function clicked() {
        document.getElementById("content-container").classList.toggle("hidden");
        nbrClick = +1;
        console.log(nbrClick);
        if (nbrClick % 2 == 0) {
            document.getElementById("burger").innerHTML = "Open";
        } else {
            document.getElementById("burger").innerHTML = "close";
        }
    }
    if (w > 800) {
        return (
            <div className="NavBar">
                <ul>
                    <li>
                        <Link to="/">Accueil</Link>
                    </li>
                    <li>
                        <Link to="/About">A propos</Link>
                    </li>
                    <li>
                        <Link to="/About">Experience</Link>
                    </li>
                </ul>
            </div>
        );
    } else {
        return (
            <div className="Responsive-NavBar">
                <div id="burger" className="burger" onClick={() => clicked()}>
                    <h2>3</h2>
                    <div id="content-container" className="hidden">
                        <ul>
                            <li>
                                <Link to="/">Accueil</Link>
                            </li>
                            <li>
                                <Link to="/About">A propos</Link>
                            </li>
                            <li>
                                <Link to="/About">Experience</Link>
                            </li>
                        </ul>
                    </div>
                </div>
            </div>
        );
    }
};

export default NavBar;
